	/**
	 * Add a TableMap instance to the database for this peer class.
	 */
	public static function buildTableMap()
	{
	  $dbMap = Propel::getDatabaseMap(BaseTblTicketPeer::DATABASE_NAME);
	  if (!$dbMap->hasTable(BaseTblTicketPeer::TABLE_NAME))
	  {
	    $dbMap->addTableObject(new TblTicketTableMap());
	  }
	}

	/**
	 * The class that the Peer will make instances of.
