	public static function doSelectJoinAllExceptRefNbrPortes(Criteria $criteria, $con = null, $join_behavior = Criteria::LEFT_JOIN)
	{
		$criteria = clone $criteria;

		// Set the correct dbName if it has not been overridden
		// $criteria->getDbName() will return the same object if not set to another value
		// so == check is okay and faster
		if ($criteria->getDbName() == Propel::getDefaultDB()) {
			$criteria->setDbName(self::DATABASE_NAME);
		}

		TblVoiturePeer::addSelectColumns($criteria);
		$startcol2 = TblVoiturePeer::NUM_HYDRATE_COLUMNS;

		TblClientPeer::addSelectColumns($criteria);
		$startcol3 = $startcol2 + TblClientPeer::NUM_HYDRATE_COLUMNS;

		RefMarquePeer::addSelectColumns($criteria);
		$startcol4 = $startcol3 + RefMarquePeer::NUM_HYDRATE_COLUMNS;

		RefMotorisationPeer::addSelectColumns($criteria);
		$startcol5 = $startcol4 + RefMotorisationPeer::NUM_HYDRATE_COLUMNS;

		$criteria->addJoin(TblVoiturePeer::ID_CLIENT, TblClientPeer::ID_CLIENT, $join_behavior);

		$criteria->addJoin(TblVoiturePeer::ID_MARQUE, RefMarquePeer::MARQUE_ID, $join_behavior);

		$criteria->addJoin(TblVoiturePeer::ID_MOTORISATION, RefMotorisationPeer::ID_MOTORISATION, $join_behavior);

		// symfony_behaviors behavior
		foreach (sfMixer::getCallables(self::getMixerPreSelectHook(__FUNCTION__)) as $sf_hook)
		{
		  call_user_func($sf_hook, 'BaseTblVoiturePeer', $criteria, $con);
		}


		$stmt = BasePeer::doSelect($criteria, $con);
		$results = array();

		while ($row = $stmt->fetch(PDO::FETCH_NUM)) {
			$key1 = TblVoiturePeer::getPrimaryKeyHashFromRow($row, 0);
			if (null !== ($obj1 = TblVoiturePeer::getInstanceFromPool($key1))) {
				// We no longer rehydrate the object, since this can cause data loss.
				// See http://www.propelorm.org/ticket/509
				// $obj1->hydrate($row, 0, true); // rehydrate
			} else {
				$cls = TblVoiturePeer::getOMClass();

				$obj1 = new $cls();
				$obj1->hydrate($row);
				TblVoiturePeer::addInstanceToPool($obj1, $key1);
			} // if obj1 already loaded

				// Add objects for joined TblClient rows

				$key2 = TblClientPeer::getPrimaryKeyHashFromRow($row, $startcol2);
				if ($key2 !== null) {
					$obj2 = TblClientPeer::getInstanceFromPool($key2);
					if (!$obj2) {
	
						$cls = TblClientPeer::getOMClass();

					$obj2 = new $cls();
					$obj2->hydrate($row, $startcol2);
					TblClientPeer::addInstanceToPool($obj2, $key2);
				} // if $obj2 already loaded

				// Add the $obj1 (TblVoiture) to the collection in $obj2 (TblClient)
				$obj2->addTblVoiture($obj1);

			} // if joined row is not null

				// Add objects for joined RefMarque rows

				$key3 = RefMarquePeer::getPrimaryKeyHashFromRow($row, $startcol3);
				if ($key3 !== null) {
					$obj3 = RefMarquePeer::getInstanceFromPool($key3);
					if (!$obj3) {
	
						$cls = RefMarquePeer::getOMClass();

					$obj3 = new $cls();
					$obj3->hydrate($row, $startcol3);
					RefMarquePeer::addInstanceToPool($obj3, $key3);
				} // if $obj3 already loaded

				// Add the $obj1 (TblVoiture) to the collection in $obj3 (RefMarque)
				$obj3->addTblVoiture($obj1);

			} // if joined row is not null

				// Add objects for joined RefMotorisation rows

				$key4 = RefMotorisationPeer::getPrimaryKeyHashFromRow($row, $startcol4);
				if ($key4 !== null) {
					$obj4 = RefMotorisationPeer::getInstanceFromPool($key4);
					if (!$obj4) {
	
						$cls = RefMotorisationPeer::getOMClass();

					$obj4 = new $cls();
					$obj4->hydrate($row, $startcol4);
					RefMotorisationPeer::addInstanceToPool($obj4, $key4);
				} // if $obj4 already loaded

				// Add the $obj1 (TblVoiture) to the collection in $obj4 (RefMotorisation)
				$obj4->addTblVoiture($obj1);

			} // if joined row is not null

			$results[] = $obj1;
		}
		$stmt->closeCursor();
		return $results;
	}

